import React, { useEffect, useState } from 'react'
import classes from './SlideShow.module.css'

const colors = ["#0088FE", "#00C49F", "#FFBB28"];

export default function SlideShow() {

    const [currentIndex, setcurrentIndex] = useState(0);

    useEffect(() => {
        setTimeout(() => {
            setcurrentIndex((prevIndex) =>
                prevIndex === colors.length - 1 ? 0 : prevIndex)
        }, 2500)
        return () => { };
    }, [currentIndex])

    return (
        <div className={classes.slideshow}>
            <div className={classes.slideshowSlider} style={{ transform: `translate3d(${-currentIndex * 100}%, 0, 0)` }}>
                {colors.map((backgrndColor, index) => (
                    <div key={index} style={{ backgrndColor }} className={classes.slide}></div>
                ))}
                <div className={classes.slideshowDots}>{
                    colors.map((_, id) => (
                        <div key={id} className={classes.slideshowDot}></div>
                    ))
                }</div>
            </div>
        </div>
    );
}
