import React from 'react'

import classes from './Input.module.css'

const Input = (props) => {
  return (
    <div className={classes.input}>
      <label htmlFor={props.input.id}>{props.label}</label>
      <input {...props.input} /> {/* ...props.input ensures that all key value pairs we recieve on props input are added (including props.input.id) as props to input */}
    </div>
  )
}

export default Input
